import React, { useState, useContext } from 'react';
import { UserContext } from "../UserContext";
import * as icons from 'react-icons/fa';
import { Link } from 'react-router-dom';
import * as api from '../library/api';

function Sidebar() {
    const User = useContext(UserContext);
    const [open, setOpen] = useState(false)
    const ToggleMobileNavbar = () => {
        setOpen(!open);
    }
    return (
        <>
            {/* <div className="sidebar" data-color="dark">
                <div className="logo">
                    <Link to='/' className="simple-text logo-normal">{User ? User.fullname : ""}</Link>
                </div>
                <div className="sidebar-wrapper ps">
                    <nav className="nav">
                        <li><Link to='/' className='nav-links' ><icons.FaHome></icons.FaHome> Home </Link></li>
                        <li><Link to='/ManageTasks' className='nav-links' ><icons.FaTasks></icons.FaTasks> Manage Tasks </Link></li>
                        <li><Link to='/ManageUsers' className='nav-links' ><icons.FaUser></icons.FaUser> Manage Users </Link></li>
                    </nav>
                </div>
            </div> */}
            {/* <nav className="navbar navbar-expand-lg navbar-transparent  bg-primary  navbar-absolute">
                <div className="navbar-wrapper">
                    <div className="navbar-toggle">
                        <button type="button" className="navbar-toggler">
                            <span className="navbar-toggler-bar bar1"></span>
                            <span className="navbar-toggler-bar bar2"></span>
                            <span className="navbar-toggler-bar bar3"></span>
                        </button>
                    </div>
                </div>
            </nav> */}
            <div className="d-flex align-items-stretch">
                <nav id="sidebar" className="">
                    <div className="sidebar-header d-flex align-items-center">
                        <div className="title">
                            <h5>{User.fullname}</h5>
                        </div>
                        <a className={open ? "burger open" : "burger"} onClick={ToggleMobileNavbar}>
                            <span></span>
                            <span></span>
                            <span></span>
                        </a>
                    </div>
                    <ul className="list-unstyled">
                        <li><Link to='/' className='' ><icons.FaHome></icons.FaHome> Home </Link></li>
                        <li><Link to='/ManageTasks' className='' ><icons.FaTasks></icons.FaTasks> Manage Tasks </Link></li>
                        <li><Link to='/ManageUsers' className='' ><icons.FaUser></icons.FaUser> Manage Users </Link></li>
                    </ul>
                </nav>
            </div>
        </>
    );
}

export default Sidebar;
