import React, { useState } from 'react';
import Project from '../../components/Project';
import * as icons from 'react-icons/fa';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.js';

function ManageTask() {
    const [taskDetails, setTaskDetails] = useState({});

    const handleSubmit = () => {
        (async () => {
            const rawResponse = await fetch('/tasks/', {
                method: 'POST',
                headers: {
                    'Accept': 'application/json',
                    'Content-Type': 'application/json'
                },

            });
            const response = await rawResponse.json();
            if (response != null && response.length > 0) {
                //setTasks(response);
            }
            //else setTasks(null);
        })();
    }

    const handleProjectChange = (newValue) => {
        setTaskDetails({ ...taskDetails, ProjectId: newValue });
    }
    console.log(taskDetails)
    return (
        <>
            <div className="modal fade bd-example-modal-lg" id="taskModal" role="dialog">
                <div className="modal-dialog modal-lg" role="document">
                    <div className="modal-content">
                        <div className="modal-header">
                            <h3>Task Details</h3>
                        </div>
                        <div className="modal-body">
                            <div className="row">
                                <div className="col-md-2">
                                    <label>Name:</label>
                                </div>
                                <div className="col-md-10">
                                    <input type="text" className="form-control" id="name" name="name" placeholder="Task's Name" />
                                </div>
                            </div>
                            <div className="row">
                                <div className="col-md-2">
                                    <label>Project:</label>
                                </div>
                                <div className="col-md-10">
                                    <Project onChange={handleProjectChange} />
                                </div>
                            </div>
                        </div>
                        <div className="modal-footer">
                            <button type="button" className="btn btn-secondary" data-dismiss="modal">Close</button>
                            <button type="button" className="btn btn-primary">Save</button>
                        </div>
                    </div>
                </div>
            </div>
        </>
    )
}

export default ManageTask
