import React from 'react';
import Select from '../../components/Select';
import * as api from '../../library/api'

class ManageTask extends React.PureComponent {
    constructor(props) {
        super(props);
        this.state = {
            taskDetails: {},
            Projects: []
        };
    }
    // const ManageTask = React.memo(() => {
    // const[taskDetails, setTaskDetails] = useState({ });
    // const[Projects, setProjects] = useState([]);

    // useEffect(() => {
    //     api.Get('/projects/', null, (response) => { if (response) setProjects(response) });
    // }, [])

    componentDidMount() {
        api.Get('/projects/', null, (response) => { if (response) this.setState({ Projects: response }) });
    }

    handleSubmit = () => {

    }

    handleProjectChange = (newValue) => {
        this.setState({ taskDetails, ProjectId: newValue });
    }

    render() {
        return (
            <>
                <div className="modal fade bd-example-modal-lg" id="taskModal" role="dialog">
                    <div className="modal-dialog modal-lg" role="document">
                        <div className="modal-content">
                            <div className="modal-header">
                                <h3>Task Details</h3>
                            </div>
                            <div className="modal-body">
                                <div className="row">
                                    <div className="col-md-2">
                                        <label>Name:</label>
                                    </div>
                                    <div className="col-md-10">
                                        <input type="text" className="form-control" id="name" name="name" placeholder="Task's Name" />
                                    </div>
                                </div>
                                <div className="row">
                                    <div className="col-md-2">
                                        <label>Project:</label>
                                    </div>
                                    <div className="col-md-10">
                                        <Select items={this.state.Projects} selectedItem={""} callback={this.handleProjectChange} />
                                    </div>
                                </div>
                            </div>
                            <div className="modal-footer">
                                <button type="button" className="btn btn-secondary" data-dismiss="modal">Close</button>
                                <button type="button" className="btn btn-primary" onClick={this.handleSubmit}>Save</button>
                            </div>
                        </div>
                    </div>
                </div>
            </>
        )
    }
}

export default ManageTask
