import React, { useState, useEffect } from "react";
import "bootstrap/dist/css/bootstrap.css";
import * as icons from "react-icons/fa";
import * as security from '../security';
import * as ultility from '../library/ultility'


const [details, setDetails] = useState({ username: "", password: "" });
function LoginForm({ FormFlip }) {
  const [keeplogin, setkeeplogin] = useState(false);
  const Login = async () => {
    security.Login(details, keeplogin);
  }
  const inputHandeler = (e) => {
    setDetails({ ...details, [e.target.name]: e.target.value })
  }
  const loginHandeler = (e) => {
    e.preventDefault();
    Login(details);
  };
  useEffect(() => {

    SetOldUsername();
  });
  return (
    <>
      <div className="front">
        <div className="content-bottom">
          <form onSubmit={loginHandeler}>
            <div className="field-group">
              <span className="fa fa-user" aria-hidden="true">
                <icons.FaUser />
              </span>
              <div className="wthree-field">
                <input type="text" name="username" onInput={e => inputHandeler(e)} placeholder="username" required />
              </div>
            </div>
            <div className="field-group">
              <span className="fa fa-lock" aria-hidden="true">
                <icons.FaLock />
              </span>
              <div className="wthree-field">
                <input type="password" name="password" onInput={e => inputHandeler(e)} placeholder="password" required />
              </div>
            </div>
            <div className="wthree-field">
              <button type="submit" className="btn"> Login </button>
            </div>
            <ul className="list-login">
              <li className="switch-agileits">
                <label className="switch"><input id="checkbox" type="checkbox" onChange={e => { setkeeplogin(!keeplogin) }} /><span className="slider round"></span>Keep logged in</label>
              </li>
              <li>
              </li>
              <li className="clearfix"></li>
            </ul>
            <ul className="list-login-bottom">
              <li>
                <a href="/#" id="Register" onClick={FormFlip}> Create Account </a>
              </li>
              <li>
                <a href="/#" className="text-right"> Forgot password? </a>
              </li>
            </ul>
          </form>
        </div>
      </div>
    </>
  );
}

export default LoginForm;

const SetOldUsername = () => {
  let encodeuname = ultility.GetCookie("uname"); let uname;
  if (encodeuname) uname = ultility.DecodeURI(encodeuname)
  if (uname) {
    setDetails({ ...details, username: uname })
    document.getElementsByName("username")[0].value = uname
  }
}
