import React, { useState, useEffect } from 'react'
import * as icons from 'react-icons/fa';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.js';
import { indexOf } from '../../backend/configs/MongoDb.config';

function ManageTasks() {
    const [Tasks, setTasks] = useState([]);
    const [TaskDetails, setTaskDetails] = useState(null);
    let Projects = null;
    const getTasks = () => {
        (async () => {
            const rawResponse = await fetch('/tasks/', {
                method: 'GET',
                headers: {
                    'Accept': 'application/json',
                    'Content-Type': 'application/json'
                }
            });
            const response = await rawResponse.json();
            if (response != null && response.length > 0) {
                setTasks(response);
            }
            else setTasks(null);
        })();
    }
    const addTask = () => {
        (async () => {
            const rawResponse = await fetch('/tasks/', {
                method: 'POST',
                headers: {
                    'Accept': 'application/json',
                    'Content-Type': 'application/json'
                },

            });
            const response = await rawResponse.json();
            if (response != null && response.length > 0) {
                setTasks(response);
            }
            else setTasks(null);
        })();
    }
    useEffect(() => {
        (async () => {
            const rawResponse = await fetch('/projects/', {
                method: 'GET',
                headers: {
                    'Accept': 'application/json',
                    'Content-Type': 'application/json'
                }
            });
            const response = await rawResponse.json();
            if (response != null && response.length > 0) {
                Projects = response;
                console.log(Projects);
            }
        })();
    }, []);
    return (
        <>
            <div>
                <label></label>
                <button className="btn btn-primary" onClick={getTasks}>Find<icons.FaSearch /></button>
                <button className="btn btn-primary" data-toggle="modal" data-target="#taskModal">Add<icons.FaPlus /></button>
            </div>
            <div className="modal fade bd-example-modal-lg" id="taskModal" role="dialog">
                <div className="modal-dialog modal-lg" role="document">
                    <div className="modal-content">
                        <div className="modal-header">
                            <h3>Task Details</h3>
                        </div>
                        <div className="modal-body">
                            <div className="row">
                                <div className="col-md-2">
                                    <label>Name:</label>
                                </div>
                                <div className="col-md-10">
                                    <input type="text" className="form-control" id="name" name="name" placeholder="Task's Name" />
                                </div>
                            </div>
                            <div className="row">
                                <div className="col-md-2">
                                    <label>Project:</label>
                                </div>
                                <div className="col-md-10">
                                    <select>
                                        {!Projects ? Projects.map((ele, index) => {
                                            return (
                                                <option value={ele._id}>{ele.Name}</option>
                                            )
                                        }) : ""}
                                    </select>
                                </div>
                            </div>
                        </div>
                        <div className="modal-footer">
                            <button type="button" className="btn btn-secondary" data-dismiss="modal">Close</button>
                            <button type="button" className="btn btn-primary">Save changes</button>
                        </div>
                    </div>
                </div>
            </div>
        </>
    )
}

export default ManageTasks
