import { response } from 'express';
import React, { Component } from 'react'

export default class Project extends Component {
    constructor(props) {
        super(props)
        this.state = {
            Projects: [],
        }
    }
    componentDidMount() {
        (async () => {
            const rawResponse = await fetch('/projects/', {
                method: 'GET',
                headers: {
                    'Accept': 'application/json',
                    'Content-Type': 'application/json'
                }
            });
            console.log()
            if (rawResponse.status === 401) {
                window.localStorage.removeItem("cUser");
                window.location.href = "/";
            }
            else {
                await rawResponse.json().then(data => {
                    console.log(data)
                    this.setState({ Projects: data });
                    this.props.onChange(data[0]._id);
                });
            }
        })();
    }

    handleChangeProject(event) {
        this.props.onChange(event.target.value);
    }

    render() {
        if (this.state.Projects.length > 1)
            return (
                <select className="form-control" onChange={this.handleChangeProject.bind(this)}>

                    {this.state.Projects.map((project, index) => {
                        return (
                            <option key={index} value={project._id}>{project.Name}</option>
                        )
                    })}
                </select>
            )
        else return null;
    }
}
