import React from 'react'

class Navlinks extends Component {
    constructor(props) {
        super(props);

        this.state = {
            navItems: [
                {
                    name: 'Home',
                    key: 0,
                    scrollChor: '',
                },
                {
                    name: 'Shows',
                    key: 1,
                    scrollChor: '#shows',
                },
                {
                    name: 'About',
                    key: 2,
                    scrollChor: '#about',
                },
                {
                    name: 'Contact',
                    key: 3,
                    scrollChor: '#contact',
                }
            ],
            activeLink: 'Home'
        };
    }


    setActive = (link) => this.setState({ active: link });


    render() {
        let navList = this.state.navItems.map((navItem) => {
            let isActive = this.state.activeLink === navItem.name;
            let navClass = isActive ? 'active nav-link' : 'nav-link';
            return (
                <NavItem
                    key={navItem.key}
                    name={navItem.name}
                    scrollChor={navItem.scrollChor}
                    navClass={navClass}
                    setActive={this.setActive}
                />
            )
        });

        return (
            { navList }
        )
    }
}

const NavItem = (props) => {

    return (
        <li>
            <Scrollchor to={props.scrollChor} className={props.navClass} onClick={() => props.setActive(props.name)}>
                {props.name}
            </Scrollchor>
        </li>
    )
}

export default Navlinks
