import React, { useCallback, useState } from 'react'
import "./LoginForm.css";
import LoginForm from './LoginForm'
import Register from './Register'
import * as ultility from '../library/ultility'

function Index() {
    const [username, setusername] = useState("")
    const FormFlip = () => {
        document.querySelector("#flipper").classList.toggle("flip");
    }
    const GetOldUsername = useCallback(() => {
        let encodeuname = ultility.GetCookie("uname"); let uname;
        if (encodeuname) uname = ultility.DecodeURI(encodeuname)
        if (uname) setusername(uname)
    }, [username])
    return (
        <>
            <section className="main">
                <div className="layer">
                    <div className="bottom-grid">
                    </div>
                    <div className="content-w3ls">
                        {/* <div className="text-center icon">
                            <span className="fa fa-html5"></span>
                        </div> */}
                        <div className="flip-container">
                            <div className="flipper" id="flipper">
                                <LoginForm FormFlip={FormFlip} GetOldUsername={GetOldUsername} OldUsername={username} />
                                <Register FormFlip={FormFlip} />
                            </div>
                        </div>
                    </div>
                </div>
            </section>
        </>
    )
}

export default Index
