import Project from '../../components/Project';
import * as icons from 'react-icons/fa';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.js';
import React, { Component } from 'react'

export default class ManageTask extends Component {
    constructor(props) {
        super(props);
        this.state = {
            ProjectId: "",
        }
    }
    addTask = () => {
        (async () => {
            const rawResponse = await fetch('/tasks/', {
                method: 'POST',
                headers: {
                    'Accept': 'application/json',
                    'Content-Type': 'application/json'
                },

            });
            const response = await rawResponse.json();
            if (response != null && response.length > 0) {
                //setTasks(response);
            }
            //else setTasks(null);
        })();
    }

    changeProject = (data) => {
        console.log(data)
    }
    render() {
        return (
            <>
                <div className="modal fade bd-example-modal-lg" id="taskModal" role="dialog">
                    <div className="modal-dialog modal-lg" role="document">
                        <form>
                            <div className="modal-content">
                                <div className="modal-header">
                                    <h3>Task Details</h3>
                                </div>
                                <div className="modal-body">
                                    <div className="row">
                                        <div className="col-md-2">
                                            <label>Name:</label>
                                        </div>
                                        <div className="col-md-10">
                                            <input type="text" className="form-control" id="name" name="name" placeholder="Task's Name" />
                                        </div>
                                    </div>
                                    <div className="row">
                                        <div className="col-md-2">
                                            <label>Project:</label>
                                        </div>
                                        <div className="col-md-10">
                                            <Project onchange={this.changeProject.bind(this)} />
                                        </div>
                                    </div>
                                </div>
                                <div className="modal-footer">
                                    <button type="button" className="btn btn-secondary" data-dismiss="modal">Close</button>
                                    <button type="submit" className="btn btn-primary">Save</button>
                                </div>
                            </div>
                        </form>
                    </div>
                </div>
            </>
        )
    }
}
