import React, { useState } from 'react'
import ManageTask from './ManageTask';
import ListTasks from './ListTasks';
import * as icons from 'react-icons/fa';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.js';

function index() {
    var Tasks = null;
    const getTasks = () => {
        (async () => {
            const rawResponse = await fetch('/tasks/', {
                method: 'GET',
                headers: {
                    'Accept': 'application/json',
                    'Content-Type': 'application/json'
                }
            });
            const response = await rawResponse.json();
            if (response != null && response.length > 0) {
                Tasks = response
            }
        })();
    }
    return (
        <>
            <div className="page-header">
                <h4>Manage Tasks</h4>
            </div>
            <div className="row">
                <section className="container-fluid">
                    <button className="btn" onClick={getTasks}>Find<icons.FaSearch /></button>
                    <button className="btn" data-toggle="modal" data-target="#taskModal">Add<icons.FaPlus /></button>
                    <ListTasks {...Tasks = Tasks} />
                    <ManageTask />
                </section>
            </div>
            {/* <div className="card">
                <div className="card-header">
                    <h3 className="card-title">List of Tasks</h3>
                </div>
                <div className="card-body">
                    <div>
                        <label></label>
                        <button className="btn btn-primary" onClick={getTasks}>Find<icons.FaSearch /></button>
                        <button className="btn btn-primary" data-toggle="modal" data-target="#taskModal">Add<icons.FaPlus /></button>
                    </div>

                </div>
            </div> */}
        </>
    )
}


export default index
