import React, { useState } from 'react'
import ManageTask from './ManageTask';
import ListTasks from './ListTasks';
import * as icons from 'react-icons/fa';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.js';

function index() {
    var Tasks = null;
    const getTasks = () => {
        (async () => {
            const rawResponse = await fetch('/tasks/', {
                method: 'GET',
                headers: {
                    'Accept': 'application/json',
                    'Content-Type': 'application/json'
                }
            });
            const response = await rawResponse.json();
            if (response != null && response.length > 0) {
                Tasks = response
            }
        })();
    }
    return (
        <div className="main-panel ps">
            <div className="content">
                <div>
                    <label></label>
                    <button className="btn btn-primary" onClick={getTasks}>Find<icons.FaSearch /></button>
                    <button className="btn btn-primary" data-toggle="modal" data-target="#taskModal">Add<icons.FaPlus /></button>
                </div>
                <ListTasks {...Tasks = Tasks} />
                <ManageTask />
            </div>
        </div>
    )
}


export default index
