import React, { useState } from "react";
import * as icons from 'react-icons/fa';
import { Link } from 'react-router-dom';

export default function Navlinks() {
    const values = [
        { id: 1, text: "Home", link: "/", icons: <icons.FaHome /> },
        { id: 2, text: "ManageTasks", link: "/ManageTasks", icons: <icons.FaTasks /> },
        { id: 3, text: "ManageUsers", link: "/ManageUsers", icons: <icons.FaUser /> }
        // { id: 4, text: "LI-4" }
    ];
    const [activeId, setActiveId] = useState();

    return (
        <>
            <ul className="list-unstyled">
                {
                    values.map((val) => (
                        <li onClick={() => setActiveId(val.id)} className={activeId === val.id ? "active" : ""}>
                            <Link to={val.link}><i>{val.icons}</i><span>{val.text}</span></Link>
                        </li>
                    ))
                }
            </ul>
        </>
    );
}
