import React from 'react'

function Select({ items }, { callback }, selectedItem) {

    if (items && items.length > 0)
        return (
            <select className="form-control" onChange={callback}>
                {items.map((item) => {
                    return (
                        <option key={item._id} value={item._id} {...selectedItem === item._id}>{item.Name}</option>
                    )
                })}
            </select>
        )
    else return (null)
}

export default Select
