import { BrowserRouter as Router, Switch, Route } from "react-router-dom";
import Navbar from "./components/Navbar";
import Sidebar from "./components/Sidebar";
import Index from "./pages/Index";
import ManageUsers from "./pages/ManageUsers";
import ManageTasks from "./pages/Tasks/index";
import LoginForm from "./login/index";
import { UserContext } from "./UserContext";
import { useState } from "react";
import * as ultility from "./library/ultility";

function App() {
  const [Open, setOpen] = useState(true)
  const ToggleMobileNavbar = isOpen => {
    setOpen(isOpen)
  }
  const GetUser = () => {
    try {
      var cookie = ultility.GetCookie("user");
      if (cookie) {
        var decode = ultility.DecodeCookie(cookie);
        var user = JSON.parse(decode);
        return user;
      } else return null;
    } catch (e) {
      return null;
    }
  };
  const [User, setUser] = useState(GetUser);
  if (User === null) {
    return (
      <>
        <LoginForm />
      </>
    );
  } else
    return (
      <>
        <UserContext.Provider value={User}>
          <Router>
            <Switch>
              <>
                <div className="d-flex align-items-stretch">
                  <Sidebar isOpen={Open} />
                  <div className="page-content active">
                    <Navbar isOpen={ToggleMobileNavbar} />
                    <Route exact path="/" component={Index} />
                    <Route path="/ManageUsers" component={ManageUsers} />
                    <Route path="/ManageTasks" component={ManageTasks} />
                  </div>
                </div>
              </>
            </Switch>
          </Router>
        </UserContext.Provider>
      </>
    );
}

export default App;
