import React, { useState, useContext, useEffect } from 'react';
import { UserContext } from "../UserContext";
import * as icons from 'react-icons/fa';
import { Link } from 'react-router-dom';
import * as api from '../library/api';
import logo from '../images/logo.png';

function Sidebar({ isOpen }) {
    const [isActive, setisActive] = useState("");
    const User = useContext(UserContext);
    useEffect(() => {
        setisActive("index");
    }, [])
    return (
        <>
            <div className="d-flex align-items-stretch">
                <nav id="sidebar" className={isOpen ? "shrinked" : ""}>
                    <div className="sidebar-header d-flex align-items-center">
                        <div className="avatar">
                            <img src={logo} alt="..." className="img-fluid rounded-circle" />
                        </div>
                        <div className="title">
                            <h5>{User.fullname}</h5>
                        </div>
                    </div>
                    <ul className="list-unstyled">
                        <li className={isActive === "index" ? "active" : ""}><Link to='/' onClick={setisActive("index")}><i><icons.FaHome></icons.FaHome></i> <span>Home</span> </Link></li>
                        <li className={isActive === "tasks" ? "active" : ""}><Link to='/ManageTasks' onClick={setisActive("tasks")} ><i><icons.FaTasks></icons.FaTasks></i> <span>Manage Tasks</span> </Link></li>
                        <li className={isActive === "users" ? "active" : ""}><Link to='/ManageUsers' onClick={setisActive("users")} ><i><icons.FaUser></icons.FaUser></i> <span>Manage Users</span> </Link></li>
                    </ul>
                </nav>
            </div>
        </>
    );
}

export default Sidebar;
