import React, { useState } from "react";
import * as icons from 'react-icons/fa';
import { Link } from 'react-router-dom';
import { useLocation } from 'react-router-dom';

export default function Navlinks() {
    const location = useLocation();
    const Items = [
        { id: "1", text: "Home", link: "/", icons: <icons.FaHome /> },
        { id: "2", text: "ManageTasks", link: "/ManageTasks", icons: <icons.FaTasks /> },
        { id: "3", text: "ManageUsers", link: "/ManageUsers", icons: <icons.FaUser /> }
        // { id: 4, text: "LI-4" }
    ];
    const GetActiveLink = () => {
        var activelink = Items.find(e => e.link === location.pathname);
        if (activelink) return activelink.id;
        else return "1";
    }
    const [activeId, setActiveId] = useState(GetActiveLink());
    return (
        <ul className="list-unstyled">
            {
                Items.map((item) => {
                    if (item.subitems) {
                        return (
                            <li>
                                <a key={item.id} href={"#" + item.id + "_dropdown"} aria-expanded="false" data-toggle="collapse" class="collapsed"></a>
                                <ul id={item.id + "_dropdown"} className="list-unstyled collapse">
                                    {item.subitems.map((subitem) => {
                                        <li key={subitem.id} onClick={() => setActiveId(item.id + "|" + subitem.id)} className={activeId.split('|')[1] === subitem.id ? "active" : ""}>
                                            <Link to={subitem.link}><i>{subitem.icons}</i><span>{subitem.text}</span></Link>
                                        </li>
                                    })}
                                </ul>
                            </li>
                        )
                    }
                    else return (
                        <li key={item.id} onClick={() => setActiveId(item.id)} className={activeId === item.id || activeId.split('|')[0] === item.id ? "active" : ""}>
                            <Link to={item.link}><i>{item.icons}</i><span>{item.text}</span></Link>
                        </li>
                    )
                })
            }
        </ul >
    );
}
