//#region imports
import { BrowserRouter as Router, Switch } from "react-router-dom";
import ProtectedRoute from './components/ProtectedRoute'
import Navbar from "./components/Navbar";
import Sidebar from "./components/Sidebar";
import Index from "./pages/Index";
import ManageUsers from "./pages/ManageUsers";
import ManageTasks from "./pages/Tasks/index";
import LoginForm from "./login/index";
import Profile from './pages/User/index'
import { UserContext } from "./UserContext";
import { useState } from "react";
import * as ultility from "./library/ultility";
import 'bootstrap';
import 'bootstrap/dist/css/bootstrap.css';
import 'bootstrap/dist/js/bootstrap.js';
//#endregion

function App() {
  const [Open, setOpen] = useState(true)
  const ToggleMobileNavbar = isOpen => {
    setOpen(isOpen)
  }
  const GetUser = () => {
    try {
      var cookie = ultility.GetCookie("user");
      if (cookie) {
        var decode = ultility.DecodeCookie(cookie);
        var user = JSON.parse(decode);
        return user;
      } else return null;
    } catch (e) {
      return null;
    }
  };
  const [User, setUser] = useState(GetUser);
  if (User === null) {
    return (
      <>
        <LoginForm />
      </>
    );
  } else
    return (
      <main>
        <UserContext.Provider value={User}>
          <Router>
            <Switch>
              <>
                <div className="d-flex align-items-stretch">
                  <Sidebar isOpen={Open} />
                  <div className="page-content active">
                    <Navbar isOpen={ToggleMobileNavbar} />
                    <ProtectedRoute exact path="/" component={Index} />
                    <ProtectedRoute path="/ManageUsers" component={ManageUsers} />
                    <ProtectedRoute path="/ManageTasks" component={ManageTasks} />
                    <ProtectedRoute path="/User" component={Profile} />
                  </div>
                </div>
              </>
            </Switch>
          </Router>
        </UserContext.Provider>
      </main>
    );
}

export default App;
