import React, { useState } from 'react';
import './LoginForm.css'
import 'bootstrap/dist/css/bootstrap.css';
import * as icons from "react-icons/fa";
import swal from 'sweetalert2';

function LoginForm() {
    function FormFlip() {
        document.querySelector("#flipper").classList.toggle("flip");
    }
    const [details, setDetails] = useState({ Username: "", Password: "", Email: "", Fullname: "" });
    const getUser = () => {
        (async () => {
            const rawResponse = await fetch('http://localhost:3001/services/', {
                method: 'POST',
                headers: {
                    'Accept': 'application/json',
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({ Action: "Login", username: details.Username, password: details.Password })
            });
            const response = await rawResponse.json();
            if (response != null && response.length > 0) {
                window.localStorage.setItem('cUser', JSON.stringify(response));
                window.location.href = '/'
            }
            else if (response !== "") {
                swal.fire({
                    type: 'error',
                    text: response
                });
            }
        })();
    }
    const register = () => {
        if (details.Username === "") {
            document.getElementById("username").focus();
        }
        else if (details.Password === "") {
            document.getElementById("password").focus();
        }
        else
            (async () => {
                const rawResponse = await fetch('http://localhost:3001/services/', {
                    method: 'POST',
                    headers: {
                        'Accept': 'application/json',
                        'Content-Type': 'application/json'
                    },
                    body: JSON.stringify({ Action: "Register", Username: details.Username, Password: details.Password, Email: details.Email, Fullname: details.Fullname })
                });
                const response = await rawResponse.json();
                if (response !== "") {
                    swal.fire({
                        type: 'error',
                        text: response.error
                    });
                }
                else alert("Succeded created user");
            })();
    }
    const loginHandeler = e => {
        e.preventDefault();
        getUser(details);
    }
    const registerHandeler = e => {
        e.preventDefault();
        register(details);
    }

    return (
        <>
            <section className="main">
                <div className="layer">
                    <div className="bottom-grid">
                        <div className="logo">
                        </div>
                    </div>
                    <div className="content-w3ls">
                        <div className="text-center icon">
                            <span className="fa fa-html5"></span>
                        </div>
                        <div className="flip-container">
                            <div className="flipper" id="flipper">
                                <div className="front">
                                    <div className="content-bottom">
                                        <form onSubmit={loginHandeler}>
                                            <div className="field-group">
                                                <span className="fa fa-user" aria-hidden="true"><icons.FaUser /></span>
                                                <div className="wthree-field">
                                                    <input type="text" name="username" onInput={e => setDetails({ ...details, Username: e.target.value })} placeholder="Username" required />
                                                </div>
                                            </div>
                                            <div className="field-group">
                                                <span className="fa fa-lock" aria-hidden="true"><icons.FaLock /></span>
                                                <div className="wthree-field">
                                                    <input type="password" name="password" onInput={e => setDetails({ ...details, Password: e.target.value })} placeholder="Password" required />
                                                </div>
                                            </div>
                                            <div className="wthree-field">
                                                <button type="submit" className="btn">Login</button>
                                            </div>
                                            <br />
                                            <ul className="list-login-bottom">
                                                <li>
                                                    <a id="Register" href="/#" onClick={FormFlip}>Create Account</a>
                                                </li>
                                                <li>
                                                    <a href="/#" className="text-right">forgot password?</a>
                                                </li>
                                            </ul>
                                        </form>
                                    </div>
                                </div>
                                <div className="back">
                                    <div className="content-bottom">
                                        <form onSubmit={registerHandeler}>
                                            <div className="field-group">
                                                <span className="fa fa-lock" aria-hidden="true"><icons.FaEnvelope /></span>
                                                <div className="wthree-field">
                                                    <input type="text" id="email" name="email" onInput={e => setDetails({ ...details, Email: e.target.value })} placeholder="Email" required />
                                                </div>
                                            </div>
                                            <div className="field-group">
                                                <span className="fa fa-lock" aria-hidden="true"><icons.FaUserCircle /></span>
                                                <div className="wthree-field">
                                                    <input type="text" id="fullname" name="fullname" onInput={e => setDetails({ ...details, Fullname: e.target.value })} placeholder="Fullname" required />
                                                </div>
                                            </div>
                                            <div className="field-group">
                                                <span className="fa fa-user" aria-hidden="true"><icons.FaUser /></span>
                                                <div className="wthree-field">
                                                    <input type="text" name="username" onInput={e => setDetails({ ...details, Username: e.target.value })} placeholder="Username" required />
                                                </div>
                                            </div>
                                            <div className="field-group">
                                                <span className="fa fa-lock" aria-hidden="true"><icons.FaLock /></span>
                                                <div className="wthree-field">
                                                    <input type="password" name="password" onInput={e => setDetails({ ...details, Password: e.target.value })} placeholder="Password" required />
                                                </div>
                                            </div>
                                            <div className="wthree-field">
                                                <button type="submit" className="btn">Register</button>
                                            </div>
                                            <br />
                                            <ul className="list-login-bottom">
                                                <li>
                                                    <a id="Login" href="/#" onClick={FormFlip}>Already have an Account</a>
                                                </li>
                                                <li>
                                                    <a href="/#" className="text-right">forgot password?</a>
                                                </li>
                                            </ul>
                                        </form>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </section>
        </>
    )
}

export default LoginForm
