import React, { useState } from 'react'

function Select({ items }, { callback }, { selectedItem }) {
    const [selected, setselected] = useState(selecteditem)
    const selecteditem = () => {
        if (selectedItem) setselected(selectedItem)
        else setselected("")
    }
    return (
        <select className="form-control" onChange={callback}>
            {
                (items && items.length > 0) ? items.map((item) => {
                    return (
                        <option key={item._id} value={item._id} {...selected === item._id ? "selected" : ""}>{item.Name}</option>
                    )
                }) : <option key={1} value={null}>--Choose--</option>
            }
        </select >
    )
}

export default Select

