import React, { useContext, useEffect } from 'react';
import { UserContext } from "../UserContext";
import * as icons from 'react-icons/fa';
import { Link } from 'react-router-dom';
import * as api from '../library/api';
import logo from '../images/logo.png';

function Sidebar({ isOpen }) {
    let location = window.location;
    let indexClass = location.pathname === "/" || location.pathname === "" ? "active" : "";
    let aboutClass = location.pathname.match(/^\/ManageTasks/) ? "active" : "";
    let contactClass = location.pathname.match(/^\/contact/) ? "active" : "";
    const User = useContext(UserContext);
    useEffect(() => {
        indexClass = location.pathname === "/" || location.pathname === "" ? "active" : "";
        aboutClass = location.pathname.match(/^\/ManageTasks/) ? "active" : "";
        contactClass = location.pathname.match(/^\/contact/) ? "active" : "";
    })
    return (
        <>
            <div className="d-flex align-items-stretch">
                <nav id="sidebar" className={isOpen ? "shrinked" : ""}>
                    <div className="sidebar-header d-flex align-items-center">
                        <div className="avatar">
                            <img src={logo} alt="..." className="img-fluid rounded-circle" />
                        </div>
                        <div className="title">
                            <h5>{User.fullname}</h5>
                        </div>
                    </div>
                    <ul className="list-unstyled">
                        <li className={indexClass}><Link to='/'><i><icons.FaHome></icons.FaHome></i> <span>Home</span> </Link></li>
                        <li className={aboutClass}><Link to='/ManageTasks'><i><icons.FaTasks></icons.FaTasks></i> <span>Manage Tasks</span> </Link></li>
                        <li className={contactClass}><Link to='/ManageUsers'><i><icons.FaUser></icons.FaUser></i> <span>Manage Users</span> </Link></li>
                    </ul>
                </nav>
            </div>
        </>
    );
}

export default Sidebar;
