import React from 'react'

function Select({ items }, { callback }) {
    if (items)
        return (
            <select className="form-control" onChange={callback}>
                {items.map((item) => {
                    return (
                        <option key={item._id} value={item._id}>{item.Name}</option>
                    )
                })}
            </select>
        )
    else return (<></>)
}

export default Select
