import React, { useState, useContext } from 'react';
import { UserContext } from "../UserContext";
import * as icons from 'react-icons/fa';
import { Link } from 'react-router-dom';
import * as api from '../library/api';

function Sidebar() {
    const User = useContext(UserContext);
    const Logout = () => {
        // api.Post("/authenticate/", details, function (response) {
        //     if (response != null) {
        //         console.log(response)
        //         window.localStorage.setItem('cUser', JSON.stringify(response));
        //         window.location.href = '/';
        //     }
        // });
        (async () => {
            const rawResponse = await fetch('/logout/', {
                method: 'POST',
                headers: {
                    'Accept': 'application/json',
                    'Content-Type': 'application/json'
                }
            });
            const response = await rawResponse.json();
            console.log(response);
            if (response === "LOGGEDOUT") {
                window.localStorage.removeItem("cUser");
                window.location.href = "/";
            }
        })();
    }
    return (
        <>
            <div className="sidebar" data-color="dark">
                <div className="logo">
                    <Link className="simple-text logo-normal">{User.fullname}</Link>
                </div>
                <div className="sidebar-wrapper ps">
                    <nav className="nav">
                        <li>
                            <Link to='/' className='nav-links' ><icons.FaHome></icons.FaHome> Home </Link>
                            <Link to='/ManageTasks' className='nav-links' ><icons.FaTasks></icons.FaTasks> Manage Tasks </Link>
                            <Link to='/ManageUsers' className='nav-links' ><icons.FaUser></icons.FaUser> Manage Users </Link>
                        </li>
                    </nav>
                </div>
            </div>
            {/* <nav className="navbar navbar-expand-lg navbar-transparent  bg-primary  navbar-absolute">
                <div className="navbar-wrapper">
                    <div className="navbar-toggle">
                        <button type="button" className="navbar-toggler">
                            <span className="navbar-toggler-bar bar1"></span>
                            <span className="navbar-toggler-bar bar2"></span>
                            <span className="navbar-toggler-bar bar3"></span>
                        </button>
                    </div>
                </div>
            </nav> */}
        </>
    );
}

export default Sidebar;
