import React, { useState } from "react";
import "bootstrap/dist/css/bootstrap.css";
import * as icons from "react-icons/fa";
import * as api from '../library/api';
import * as ultility from '../library/ultility';

function LoginForm({ FormFlip }) {
  const [details, setDetails] = useState({
    username: "",
    password: "",
    Email: "",
    Fullname: "",
  });
  const Login = () => {
    api.Post("/authenticate/", details, function (response) {
      if (response) {
        var json = JSON.stringify(response);
        var encode = ultility.EncodeCookie(json);
        ultility.SetCookie("user", encode, 30);
        window.location.href = '/'
      }
    });
  }
  const loginHandeler = (e) => {
    e.preventDefault();
    Login(details);
  };

  return (
    <>
      <div className="front">
        <div className="content-bottom">
          <form onSubmit={loginHandeler}>
            <div className="field-group">
              <span className="fa fa-user" aria-hidden="true">
                <icons.FaUser />
              </span>
              <div className="wthree-field">
                <input
                  type="text"
                  name="username"
                  onInput={(e) =>
                    setDetails({ ...details, username: e.target.value })
                  }
                  placeholder="username"
                  required
                />
              </div>
            </div>
            <div className="field-group">
              <span className="fa fa-lock" aria-hidden="true">
                <icons.FaLock />
              </span>
              <div className="wthree-field">
                <input
                  type="password"
                  name="password"
                  onInput={(e) =>
                    setDetails({ ...details, password: e.target.value })
                  }
                  placeholder="password"
                  required
                />
              </div>
            </div>
            <div className="wthree-field">
              <button type="submit" className="btn">
                Login
              </button>
            </div>
            <br />
            <ul className="list-login-bottom">
              <li>
                <a id="Register" href="/#" onClick={FormFlip}>
                  Create Account
                </a>
              </li>
              <li>
                <a href="/#" className="text-right">
                  forgot password?
                </a>
              </li>
            </ul>
          </form>
        </div>
      </div>
    </>
  );
}

export default LoginForm;
