import React, { useState } from "react";
import * as icons from 'react-icons/fa';
import { Link } from 'react-router-dom';
import { useLocation } from 'react-router-dom';

export default function Navlinks() {
    const location = useLocation();
    console.log(location.pathname)
    const Items = [
        { id: 1, text: "Home", link: "/", icons: <icons.FaHome /> },
        { id: 2, text: "ManageTasks", link: "/ManageTasks", icons: <icons.FaTasks /> },
        { id: 3, text: "ManageUsers", link: "/ManageUsers", icons: <icons.FaUser /> }
        // { id: 4, text: "LI-4" }
    ];
    const [activeId, setActiveId] = useState(1);
    const GetActiveLink = () => {
        var activelink = Items.find(e => e.link === location.pathname)
    }
    return (
        <ul className="list-unstyled">
            {
                Items.map((item) => (
                    <li key={item.id} onClick={() => setActiveId(item.id)} className={activeId === item.id ? "active" : ""}>
                        <Link to={item.link}><i>{item.icons}</i><span>{item.text}</span></Link>
                    </li>
                ))
            }
        </ul>
    );
}
