import React, { useState } from 'react'
import * as icons from 'react-icons/fa';
import 'bootstrap/dist/css/bootstrap.min.css';

function ManageTasks() {
    const [Tasks, setTasks] = useState([]);
    const [TaskDetails, setTaskDetails] = useState(null);
    const getTasks = () => {
        (async () => {
            const rawResponse = await fetch('/tasks/', {
                method: 'GET',
                headers: {
                    'Accept': 'application/json',
                    'Content-Type': 'application/json'
                }
            });
            const response = await rawResponse.json();
            if (response != null && response.length > 0) {
                setTasks(response);
            }
            else setTasks(null);
        })();
    }
    const addTask = () => {
        (async () => {
            const rawResponse = await fetch('/tasks/', {
                method: 'POST',
                headers: {
                    'Accept': 'application/json',
                    'Content-Type': 'application/json'
                },

            });
            const response = await rawResponse.json();
            if (response != null && response.length > 0) {
                setTasks(response);
            }
            else setTasks(null);
        })();
    }
    return (
        <>
            <div>
                <label></label>
                <button className="btn btn-primary" onClick={getTasks}>Find<icons.FaSearch /></button>
                <button className="btn btn-primary" data-toggle="modal" data-target="#exampleModal">Add<icons.FaPlus /></button>
            </div>
            <div className="modal" role="dialog">
                <div class="modal-dialog" role="document">
                    <div class="modal-content">
                        <div class="modal-header">
                            <h3>Task Details</h3>
                        </div>
                    </div>
                </div>
            </div>
        </>
    )
}

export default ManageTasks
