import * as api from "./library/api";
import * as ultility from "./library/ultility";
import CustomeError from './error'
import { error } from "jquery";

export const GetUser = () => {
    try {
        var cookie = ultility.GetCookie("user");
        if (cookie) {
            var decode = ultility.DecodeCookie(cookie);
            var user = JSON.parse(decode);
            if (user instanceof CustomeError) throw new CustomeError(user)
            return user;
        } else return null;
    } catch (e) {
        throw new error(e);
    }
};

export const Authenticate = (data) => {
    var json = JSON.stringify(data);
    var encode = ultility.EncodeCookie(json);
    ultility.SetCookie("user", encode, 30);
    return true;
}

export const Login = async (credentials) => {
    let response = await api.Post("/authenticate/", credentials)
    if (response && response.status === 200) {
        Authenticate(response)
        window.location.href = '/'
    }
}

export const Logout = async () => {
    await api.Post("/Logout/", null)
    ultility.DeleteCookie("user");
    window.location.href = '/Login'
}

export const CheckAuthentication = async () => {
    var user = GetUser();
    if (user) return true;
    else {
        user = await api.Post('/authenticate', null)
        if (user) {
            Authenticate(user);
            return true;
        }
    }
    return false;
}
