import React, { useState } from "react";
import * as icons from 'react-icons/fa';
import { Link } from 'react-router-dom';

class Navlinks extends React.PureComponent {
    constructor(props) {
        super(props);
        this.state = {
            activeId: "",
            Items: [
                { id: "1", text: "Home", link: "/", icons: <icons.FaHome /> },
                { id: "2", text: "ManageTasks", link: "/ManageTasks", icons: <icons.FaTasks /> },
                { id: "3", text: "ManageUsers", link: "/ManageUsers", icons: <icons.FaUser /> }
            ]
        };
    }

    componentDidMount() {
        let activeid = "";
        var activelink = this.state.Items.find(e => e.link === this.props.pathname);
        if (activelink) activeid = activelink.id;
        else activeid = "1";
        this.setState({
            activeId: activeid,
        })
    }

    setActiveId(id) {
        this.setState({
            activeId: id,
        })
    }

    render() {
        console.log(this.props.location)

        return (
            <ul className="list-unstyled">
                {
                    this.state.Items.map((item) => {
                        if (item.subitems) {
                            return (
                                <li>
                                    <a key={item.id} href={"#" + item.id + "_dropdown"} aria-expanded="false" data-toggle="collapse" class="collapsed"></a>
                                    <ul id={item.id + "_dropdown"} className="list-unstyled collapse">
                                        {item.subitems.map((subitem) => {
                                            <li key={subitem.id} onClick={() => this.setActiveId(item.id + "|" + subitem.id)} className={this.state.activeId.split('|')[1] === subitem.id ? "active" : ""}>
                                                <Link to={subitem.link}><i>{subitem.icons}</i><span>{subitem.text}</span></Link>
                                            </li>
                                        })}
                                    </ul>
                                </li>
                            )
                        }
                        else return (
                            <li key={item.id} onClick={() => this.setActiveId(item.id)} className={this.state.activeId === item.id || this.state.activeId.split('|')[0] === item.id ? "active" : ""}>
                                <Link to={item.link}><i>{item.icons}</i><span>{item.text}</span></Link>
                            </li>
                        )
                    })
                }
            </ul >
        );
    }
}


export default Navlinks;
