import React, { useContext, useState } from 'react';
import { UserContext } from "../UserContext";
import * as icons from 'react-icons/fa';
import { Link } from 'react-router-dom';
import * as api from '../library/api';
import logo from '../images/logo.png';
import { Navlinks } from './Navlinks';

function Sidebar({ isOpen }) {
    const User = useContext(UserContext);
    return (
        <>
            <div className="d-flex align-items-stretch">
                <nav id="sidebar" className={isOpen ? "shrinked" : ""}>
                    <div className="sidebar-header d-flex align-items-center">
                        <div className="avatar">
                            <img src={logo} alt="..." className="img-fluid rounded-circle" />
                        </div>
                        <div className="title">
                            <h5>{User.fullname}</h5>
                        </div>
                    </div>
                    <ul className="list-unstyled">
                        {/* <li className="nav-item active"><Link to='/'><i><icons.FaHome></icons.FaHome></i><span>Home</span></Link></li>
                        <li className="nav-item"><Link to='/ManageTasks'><i><icons.FaTasks></icons.FaTasks></i><span>Manage Tasks</span></Link></li>
                        <li className="nav-item"><Link to='/ManageUsers'><i><icons.FaUser></icons.FaUser></i><span>Manage Users</span></Link></li> */}
                        <Navlinks options={["Link 1", "Link 2", "Link 3"]} />
                    </ul>
                </nav>
            </div>
        </>
    );
}

export default Sidebar;
