import * as api from "./library/api";
import * as ultility from "./library/ultility";
import swal from 'sweetalert2'

export const GetUser = () => {
    try {
        var cookie = ultility.GetCookie("user");
        var session = ultility.GetSession("user");
        console.log(cookie)
        if (cookie) {
            var decode = ultility.DecodeURI(cookie);
            var user = JSON.parse(decode);
            return user;
        } else if (session) {
            var decode = ultility.DecodeURI(session);
            var user = JSON.parse(decode);
            return user;
        }
        else return null;
    } catch (e) {

    }
};

export const Authenticate = (data, savecookie) => {
    var json = JSON.stringify(data);
    var encode = ultility.EncodeURI(json);
    if (savecookie) ultility.SetCookie("user", encode, 30);
    else ultility.SetSession("user", encode)
    return true;
}

export const Login = async (credentials, savecookie) => {
    try {
        const response = await api.Post("/authenticate/", credentials)
        if (response && response.type === 1) {
            Authenticate(response.data, savecookie);
            ultility.DeleteCookie("uname");
            window.location.href = '/'
        }
        else swal.fire({
            type: "error",
            html: response.message
        })
    }
    catch (e) {
        throw e;
    }
}

export const Logout = async () => {
    await api.Post("/Logout/", null);
    var user = JSON.parse(ultility.DecodeURI(ultility.GetCookie("user")));
    ultility.SetCookie("uname", ultility.EncodeURI(user.username));
    ultility.DeleteCookie("user"); ultility.DeleteSession("user");
    window.location.href = '/Login'
}

export const CheckAuthentication = async () => {
    var user = GetUser();
    if (user) return true;
    else {
        user = await api.Post('/authenticate', null)
        if (user) {
            Authenticate(user);
            return true;
        }
    }
    return false;
}
